INSERT INTO `wp_exames` (`id`, `nome`, `text`, `img`) VALUES
(5, 'Teste Ergométrico Computadorizado', 'Exame realizado com esforço físico que auxilia na investigação de sinais sugestivos de obstruções nas artérias coronárias, anormalidades no ritmo cardíaco, alterações na pressão arterial e sintomas desencadeados pelo esforço, como a dor no peito. Não invasivo e geralmente indolor.\r\n\r\nPreparo: Alimentar-se até 2 horas antes do exame. Não vir em jejum. Utilizar roupas adequadas para traje de atividade física e tênis. Solicitar ao médico orientações sobre suspensão de medicamento betabloqueador. Trazer Eletrocardiograma (ECG) recente. Mulheres devem evitar uso de vestidos, optando por blusa e calça.\r\nEntrega do Resultado: Imediata.', 'none'),
(4, 'Ecocardiograma Fetal Colorido', 'Exame de ultrassom realizado em gestantes que detecta possíveis alterações no coração do feto ainda no ventre materno, como cardiopatias congênitas e arritmias, auxiliando no diagnóstico de alterações que possam necessitar de tratamento intra-uterino ou intervenção cirúrgica logo após o nascimento. Não invasivo e indolor.\r\n\r\nPreparo: Sem necessidade.\r\nEntrega do Resultado: Imediata.', 'none'),
(6, 'MAPA 24 horas (Monitorização Ambulatorial da Pressão Arterial)', 'Exame realizado através de medidor de Pressão Arterial portátil, que será instalado no paciente e registrará as alterações na pressão durante as 24 horas de duração do procedimento, efetuando medidas a cada 20 minutos no período diurno e 30 minutos no período noturno. O paciente seguirá com suas rotinas normalmente, evitando movimentos apenas nos momentos de aferição e retornará à clínica no dia seguinte para a retirada do aparelho.\r\n\r\nPreparo: Pelo fato da impossibilidade ao banho durante o exame, recomenda-se que o paciente venha à clínica com sua higiene já realizada, utilizando roupas largas e confortáveis. Mulheres devem evitar uso de vestidos justos e optar por calça e camisas largas. Proibido molhar o aparelho e evitar movimentar-se durante as aferições. Trazer receita com as medicações em uso.\r\n\r\nEntrega do Resultado: 5 dias úteis após a retirada do aparelho.', 'none'),
(7, 'Ecografia de Carótidas com Doppler Colorido', 'Descrição: Exame de ultrassom que possibilita verificar mudanças no espessamento do revestimento das artérias carótidas e possíveis obstruções, impedindo o fluxo de sangue adequado até o cérebro. Realizado no pescoço do paciente. Não invasivo e indolor.\r\n\r\nPreparo: Sem necessidade.\r\nEntrega do Resultado: Imediata.', 'none'),
(8, 'Ecografia de Vertebrais com Doppler Colorido', 'Exame de ultrassom que possibilita verificar mudanças no espessamento do revestimento das artérias vertebrais e possíveis obstruções, impedindo o fluxo de sangue adequado até o cérebro. Realizado no pescoço do paciente. Não invasivo e indolor.\r\n\r\nPreparo: Sem necessidade.\r\nEntrega do Resultado: Imediata.', 'none'),
(9, 'Ecocardiograma com Estresse Farmacológico', 'Exame de ultrassom, associado a medicações, que possibilita o estudo do músculo cardíaco em repouso e sob esforço máximo. Permite avaliar indícios de obstrução das artérias coronarianas e auxilia no diagnóstico e prognóstico para avaliação de risco cardiovascular. Pouco invasivo e geralmente indolor.\r\n\r\nPreparo: Jejum de 4 horas antes do exame, inclusive de água. Manutenção da medicação normal e suspensão de medicamentos betabloqueadores por 3 dias antes do exame (sob orientação médica ou adquirida no ato da marcação). Trazer resultados de exames cardiológicos anteriores e receita médica. Mulheres devem evitar uso de vestidos e dar preferência à blusa e calça. Necessita de acompanhante e repouso de 20 minutos na clínica após o exame.\r\n\r\nEntrega do Resultado: Imediata.', 'none'),
(10, 'ECG (Eletrocardiograma Computadorizado)', 'Exame realizado em repouso que avalia a variação da atividade elétrica do coração e auxilia na detecção de alterações como arritmias, síndromes isquêmicas e possível infarto agudo do miocárdio. Não invasivo e indolor.\r\n\r\nPreparo: Sem necessidade.\r\nEntrega do Resultado: Imediata.', 'none'),
(11, 'Holter 24 horas (Avaliação eletrocardiográfica ambulatorial dinâmica)', 'Exame realizado através de monitor portátil que será instalado no paciente e registrará as atividades elétricas do coração durante as 24 horas de duração do procedimento. O paciente seguirá com suas rotinas normalmente e retornará à clínica no dia seguinte para a retirada do aparelho.\r\nPreparo: Pelo fato da impossibilidade ao banho durante o exame, recomenda-se que o paciente venha à clínica com sua higiene já realizada, utilizando roupas largas e confortáveis. Mulheres devem evitar uso de vestidos justos e optar por calça e camisa. Proibido molhar o aparelho e realizar exames de radiografia e ressonância durante o procedimento. Pacientes em uso de Marca-passo devem levar a carteirinha do aparelho. Trazer receita com as medicações em uso.\r\n\r\nEntrega do Resultado: 5 dias úteis após a retirada do aparelho.', 'none'),
(12, 'MRPA (Monitorização Residencial da Pressão Arterial)', 'Exame de registro da Pressão Arterial realizado pelo próprio paciente ou acompanhante, em sua residência no período de 4 dias, onde as medidas são efetuadas em 4 momentos do dia do paciente. No caso do MRPA, o paciente não precisa dormir utilizando o aparelho.\r\n\r\nPreparo: Para o caso de pacientes idosos trazer acompanhante que receberá as instruções de funcionamento do aparelho e orientações de uso durante os 4 dias de sua duração. Trazer receita com as medicações em uso. O aparelho será devolvido à clínica no dia subseqüente ao fim do exame em horário agendado.\r\nEntrega do Resultado: 5 dias úteis após a devolução do aparelho.', 'none'),
(13, 'Ecocardiograma Bidimensional com Doppler Colorido', 'Exame de ultrassom que possibilita avaliar a saúde do coração do paciente, verificando anomalias funcionais e morfológicas do órgão, alterações na estrutura e em suas funções. Não invasivo e indolor.\r\n\r\nPreparo: não há necessidade.\r\nEntrega do resultado: Imediata.', 'none');

